package loops.com;

public class largestofthree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5,b=20,c=10;
		
	if(a>b && a>c) 
	{
		System.out.println("a is larger");
	}
	else if(b>a && b>c)
	{
		System.out.println("b is larger");
		
	}
	else
	{
		System.out.println("c is larger");
	}
	
	}
}
